changeSet.sql:
DROP SEQUENCE IF EXISTS detailed_lease_contract_seq;
CREATE SEQUENCE IF NOT EXISTS detailed_lease_contract_seq START WITH (SELECT COALESCE(MAX(id), 0) + 1 FROM detailed_lease_contract);
DROP SEQUENCE IF EXISTS ifrs16lease_contract_seq;

rollback.sql:
DROP SEQUENCE IF EXISTS ifrs16lease_contract_seq;
CREATE SEQUENCE IF NOT EXISTS ifrs16lease_contract_seq START WITH (SELECT COALESCE(MAX(id), 0) + 1 FROM ifrs16lease_contract);
DROP SEQUENCE IF EXISTS detailed_lease_contract_seq;
